Scripts.OnTextChanged:
--[[local scrollBar = _G["SoundtrackFrame_EventScriptScrollBar"]
--SoundtrackFrame_EventScript:UpdateScrollChildRect();
local min;
local max;
min, max = scrollBar:GetMinMaxValues();
if ( max > 0 and (self.max ~= max) ) then
self.max = max;
scrollBar:SetValue(max);
end
--]]

